import React from "react";
import styled from "styled-components";
import ServiceBox from "./ServiceBox";

const ServiceContent = () => {
  return (
    <Container>
      <div className="bg-stripes"></div>
      <p className="service-intro">
        We provide best-in-class services in your budget
      </p>
      <ServiceGrid>
        <ServiceBox
          name="Engine Swap / Repair"
          bg="/static/images/service/engine.jpg"
        />
        <ServiceBox
          name="Electrical Repairs"
          bg="/static/images/service/electric.jpg"
        />
        <ServiceBox
          name="Brakes Check"
          bg="/static/images/service/brakes.jpg"
        />
        <ServiceBox name="Oil Change" bg="/static/images/service/oil.jpg" />
        <ServiceBox
          name="Air Filter Check"
          bg="/static/images/service/filter.jpg"
        />
        <ServiceBox
          name="General Services"
          bg="/static/images/service/general.jpg"
        />
      </ServiceGrid>
    </Container>
  );
};

export default ServiceContent;

const Container = styled.div`
  width: 100vw;
  background-color: #151515;
  position: relative;
  /* height: 100vh; */
  .service-intro {
    font-family: Quicksand;
    font-style: normal;
    font-weight: 500;
    font-size: 50px;
    line-height: 62px;
    text-align: center;
    letter-spacing: 0.055em;
    color: #fcfcfc;
    width: 40%;
    margin: 0 auto;
    /* margin-top: 10vh; */
  }
  .bg-stripes {
    position: absolute;
    /* z-index: -10; */
    background: url("/static/images/service/stripes.svg") bottom center
      no-repeat;

    top: 40vh;
    left: 0;
    background-size: cover;
    width: 120%;
    height: 100%;
    z-index: -10;
  }
`;

const ServiceGrid = styled.div`
  z-index: 10;
  margin: 15vh 0;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-gap: 95px;
`;
