import React from "react";
import styled from "styled-components";
//
import Slider from "react-slick";

const Testimonials = () => {
  let settings = {
    dots: true,
    centerMode: true,
    centerPadding: "0px",
    cenetMargin: "10px",
    slidesToShow: 3,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          arrows: false,
          centerMode: true,
          centerPadding: "40px",
          slidesToShow: 3
        }
      },
      {
        breakpoint: 480,
        settings: {
          arrows: false,
          centerMode: true,
          centerPadding: "40px",
          slidesToShow: 1
        }
      }
    ]
  };
  return (
    <Container>
      <Slider {...settings}>
        <TestCard>
          <p>Test</p>
        </TestCard>
        <TestCard>
          <p>Test</p>
        </TestCard>
        <TestCard>
          <p>Test</p>
        </TestCard>
        <TestCard>
          <p>Test</p>
        </TestCard>
        <TestCard>
          <p>Test</p>
        </TestCard>
      </Slider>
    </Container>
  );
};

export default Testimonials;

const Container = styled.div`
  width: 80%;
  margin: 0 auto;
  height: 60vh;
  background-color: #fff;
`;

const TestCard = styled.div`
  width: 25vw;
  height: 25vh;
  background-color: black;
  margin: 0 10px;
  padding: 10px;
  border: 1px red solid;
  p {
    color: white;
    font-size: 24px;
  }
`;
