import React from "react";
import styled from "styled-components";
import NavbarComp from "../components/UI/NavbarComp";
import Head from "next/head";
import ServiceBox from "./../components/service/ServiceBox";

const services = () => {
  return (
    <div>
      <Head>
        <title>Service</title>
        <link
          href="https://fonts.googleapis.com/css?family=Poppins|Quicksand|Racing+Sans+One&display=swap"
          rel="stylesheet"
        />
      </Head>
      <Container>
        <NavbarComp />
        <BannerBG>
          <div className="banner-title">
            <p>Services</p>
          </div>
        </BannerBG>
        <div className="bg-stripes"></div>
        <p className="service-intro">
          We provide best-in-class services in your budget
        </p>
        <ServiceGrid>
          <ServiceBox
            name="Engine Swap / Repair"
            bg="/static/images/service/engine.jpg"
          />
          <ServiceBox
            name="Electrical Repairs"
            bg="/static/images/service/electric.jpg"
          />
          <ServiceBox
            name="Brakes Check"
            bg="/static/images/service/brakes.jpg"
          />
          <ServiceBox name="Oil Change" bg="/static/images/service/oil.jpg" />
          <ServiceBox
            name="Air Filter Check"
            bg="/static/images/service/filter.jpg"
          />
          <ServiceBox
            name="General Services"
            bg="/static/images/service/general.jpg"
          />
        </ServiceGrid>
      </Container>
      <style jsx global>{`
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
};

export default services;

const Container = styled.div`
  width: 100vw;
  line-height: 1.3;
  margin: 0;
  background-color: #151515;
  height: 400vh;
  position: relative;
  z-index: -20;
  overflow: hidden;
  .bg-stripes {
    position: absolute;
    /* z-index: -10; */
    background: url("/static/images/service/stripes.svg") bottom center
      no-repeat;

    top: 60vh;
    left: 0;
    background-size: cover;
    width: 120%;
    height: 100%;
    z-index: -10;
  }
  .service-intro {
    font-family: Quicksand;
    font-style: normal;
    font-weight: 500;
    font-size: 50px;
    line-height: 62px;
    text-align: center;
    letter-spacing: 0.055em;
    color: #fcfcfc;
    width: 40%;
    margin: 0 auto;
    margin-top: 10vh;
  }
`;
const BannerBG = styled.div`
  width: 100vw;
  height: 60vh;
  background: url("/static/images/service/service-banner.png") bottom center
    no-repeat;
  background-size: cover;
  .banner-title {
    height: 60%;
    width: 100%;
    background: rgba(67, 67, 67, 0.44);
    height: 60%;
    transform: translate3d(0, 35%, 0);
  }
  p {
    font-family: Quicksand;
    font-style: normal;
    font-weight: normal;
    font-size: 100px;
    line-height: 125px;
    text-align: center;
    letter-spacing: 0.725em;
    transform: translate3d(0, 65%, 0);
    color: #ffffff;
  }
`;

const ServiceGrid = styled.div`
  z-index: 10;
  margin-top: 15vh;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-gap: 95px;
`;
