import React, { useState } from "react";
import styled from "styled-components";
import media from "styled-media-query";
import Link from "next/link";
import { motion } from "framer-motion";

const NavbarComp = () => {
  const [mobileWidth, setMobileWidth] = useState(false);

  React.useEffect(() => {
    window.addEventListener("resize", handleScroll);
  }, []);

  const handleScroll = e => {
    const windowWidth = window.innerWidth;
    console.log(windowWidth);
    if (windowWidth < 768) {
      setMobileWidth(true);
    } else {
      setMobileWidth(false);
    }
  };

  return (
    <>
      {mobileWidth ? (
        <MobileMenu>
          <svg
            width="68"
            height="42"
            viewBox="0 0 68 42"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <rect width="68" height="8" rx="4" fill="white" />
            <rect y="17" width="68" height="8" rx="4" fill="white" />
            <rect y="34" width="68" height="8" rx="4" fill="white" />
          </svg>
        </MobileMenu>
      ) : (
        <NavigationComp>
          {" "}
          <Link href="/">
            <motion.a whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
              Home
            </motion.a>
          </Link>
          <Link href="/about">
            <motion.a whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
              About
            </motion.a>
          </Link>
          <Link href="/services">
            <motion.a whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
              Services
            </motion.a>
          </Link>
          <Link href="/contact">
            <motion.a whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
              Contact
            </motion.a>
          </Link>{" "}
        </NavigationComp>
      )}
    </>
  );
};

export default NavbarComp;

const NavigationComp = styled.div`
  width: 80%;
  height:90px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  color: white;
  justify-content: center;
  align-content: center;
  padding-top: 4vh;
  z-index: 100;
  a {
    color: white;
    text-decoration: none;
    font-family: "Quicksand", sans-serif;
    font-size: 1.5rem;
    line-height: 132%;
    text-align: center;
    letter-spacing: 0.27em;
    justify-self: center;
    align-self: center;
    /* transition: all 0.2s ease-in-out; */
    cursor: pointer;
  }
  /* a:hover {
    color: #fcb801;
    transform: translateY(-1vh);
    letter-spacing: 0.35em;
  } */

  ${media.lessThan("small")`
    /* screen width is less than 450px (small) */
 /* display:none; */
  `}
  ${media.between("small", "medium")`
    /* screen width is between 450px and 768px (small to medium) */
grid-gap:20px;
  `}

    /* screen width is between 768px (medium) and 1170px (large) */
  ${media.between("medium", "large")`
    grid-gap:40px;
  `}

  ${media.greaterThan("large")`
    /* screen width is greater than 1170px (large) */
  
  `}
`;

const MobileMenu = styled.div`
  width: 50%;
  height: 50px;
  svg {
    position: absolute;
    top: 1vh;
    right: 2vw;
  }
`;
