import React from "react";
import styled from "styled-components";
//
import Slider from "react-slick";

const Testimonials = () => {
  let settings = {
    dots: true,
    centerMode: true,
    centerPadding: "-150px",
    slidesToShow: 3,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          arrows: false,
          centerMode: true,
          centerPadding: "40px",
          slidesToShow: 3
        }
      },
      {
        breakpoint: 480,
        settings: {
          arrows: false,
          centerMode: true,
          centerPadding: "40px",
          slidesToShow: 1
        }
      }
    ]
  };
  return (
    <Container>
      <Slider {...settings}>
        <TestCard>
          <p>Test</p>
        </TestCard>
        <TestCard></TestCard>
        <TestCard></TestCard>
      </Slider>
    </Container>
  );
};

export default Testimonials;

const Container = styled.div`
  width: 100%;
  height: 60vh;
  background-color: #fff;
`;

const TestCard = styled.div`
  width: 25vw;
  height: 25vh;
  background-color: black;
  p {
    color: white;
    font-size: 24px;
  }
`;
