import React from "react";
import styled from "styled-components";

const AboutContent = () => {
  return (
    <Container>
      <BGstripes></BGstripes>
      <SamCard>
        <p className="name">Sam</p>
        <p className="title">The Chief</p>
        <p className="desc">
          The CEO of Sam’s auto. Great mechanic and solution provider.Friendly
          and great to have a chat with. Enjoy’s BBQ
        </p>
      </SamCard>
    </Container>
  );
};

export default AboutContent;

const Container = styled.div`
  width: 100vw;
  background-color: #fff;
  height: 200vh;
  z-index: 20;
  position: relative;
  overflow: hidden;
`;

const BGstripes = styled.div`
  position: absolute;
  /* z-index: -10; */
  background: url("/static/images/about/stripes.svg") bottom center no-repeat;

  top: -10vh;
  left: 0;
  background-size: cover;
  width: 110%;
  height: 160%;
  z-index: -10;
`;

const SamCard = styled.div`
  width: 30vw;
  height: 30vh;
  background: #ffffff;
  box-shadow: 6px 6px 4px rgba(0, 0, 0, 0.25), -6px 4px 4px rgba(0, 0, 0, 0.25);
  border-radius: 5px;
  font-family: Poppins;
  font-style: thin;
  color: #000000;
  text-align: center;
  /* transform: translate3d(50%, -35%, 0); */
  z-index: 10;
  position: absolute;
  top: -10vh;
  left: 40vw;
  .name {
    font-weight: 300;
    font-size: 48px;
  }
`;
