import React, { useState } from "react";
import styled from "styled-components";

const ContactForm = () => {
  const [name, setName] = useState("");

  return (
    <Container>
      <FormBox>
        <p>Contact Form</p>
        <InputGroup>
          <input
            className="name-input"
            type="text"
            name="name"
            value={name}
            placeholder="Name"
            onChange={name => setName(name.target.value)}
          />
        </InputGroup>
      </FormBox>
    </Container>
  );
};

export default ContactForm;

const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: #151515;
  padding: 10vh 0;
`;

const FormBox = styled.div`
  width: 50%;
  height: 80vh;
  margin: 0 auto;
  background-color: #efefef;
  border-radius: 15px;
`;

const InputGroup = styled.div`
  width: 90%;
  margin: 2vh auto;

  input {
    width: 100%;
    height: 5vh;
    margin: 1vh 0;
    padding-left: 2vw;
    border: 1px gray solid;
    border-radius: 5px;
  }
  textarea {
    width: 100%;
    margin: 1vh 0;
    padding-left: 2vw;
    border: 1px gray solid;
    border-radius: 5px;
    padding-top: 1vh;
  }
`;
